mapper.select:
SELECT t1.id,t1.compare_type,t1.inner_code1,t1.inner_code2,t1.outer_code,t1.inner_model1, t1.inner_model2,t1.outer_model,t1.whole_model,t1.code_begin,t1.update_time, v5.code_value brand FROM ( SELECT t.id, v1.code_value compare_type, t.inner_code1, t.inner_code2, t.outer_code, t.inner_model1, t.inner_model2, t.outer_model, t.whole_model, t.code_begin, t.update_time,t.brand FROM hx_code_bar t LEFT JOIN v_code v1 ON t.compare_type=v1.code_key AND v1.code_id='dzlx' )t1 LEFT JOIN v_code v5 on t1.brand=v5.code_key and v5.code_id='pp' WHERE 1=1 and t1.compare_type = #{param.compareType} and t1.inner_code1 like CONCAT('%',#{param.innerCode1},'%') and t1.inner_code2 like CONCAT('%',#{param.innerCode2},'%') and t1.outer_code like CONCAT('%',#{param.outerCode},'%') and t1.inner_model1 like CONCAT('%',#{param.innerModel1},'%') and t1.inner_model2 like CONCAT('%',#{param.innerModel2},'%') and t1.outer_model like CONCAT('%',#{param.outerModel},'%') and t1.whole_model like CONCAT('%',#{param.wholeModel},'%') and t1.code_begin like CONCAT('%',#{param.codeBegin},'%') and t1.brand like CONCAT('%',#{param.brand},'%')
and t1.update_time >= #{param.ksrq}
and t1.update_time <= #{param.jsrq}
