mapper.select:
SELECT product.classify_name, fee_id, fee.whole_fee, fee.inner_fee, fee.outer_fee, hx_code_setting.code_value as operation_type FROM hx_fee_movemachine AS fee INNER JOIN hx_product_classify AS product ON fee.classify_code = product.classify_code , hx_code INNER JOIN hx_code_setting ON hx_code_setting.code_id = hx_code.code_id WHERE fee.classify_code = product.classify_code and hx_code_setting.code_id='mcm' AND hx_code_setting.code_key = fee.operation_type
and fee.classify_code like CONCAT('%',#{param.classifyCode},'%')
and fee.whole_fee = #{param.wholefee, jdbcType=FLOAT}
and fee.inner_fee = #{param.innerfee, jdbcType=FLOAT}
and fee.outer_fee = #{param.outerfee, jdbcType=FLOAT}
and hx_code_setting.code_key like CONCAT('%',#{param.operationType,jdbcType=VARCHAR},'%')

mapper.update:
UPDATE  hx_fee_movemachine SET classify_code=#{classifyCode}, whole_fee=#{wholefee}, inner_fee=#{innerfee},
	outer_fee=#{outerfee}, modify_username=#{modifyUsername},modify_date=#{modifyDate},
	operation_type=  #{operationType}
	WHERE fee_id = #{feeID}
