mapper.select:
SELECT d.fitting_code, c.code_value AS type, m.list_number, m. COMMENT, m.update_time, v3.user_name create_person, IF ( ISNULL(d.audit_count), d.apply_count, d.audit_count ) count, d.price, hx_fitting_detail.fitting_name, v.`name` send_company, v2.`name` receive_company FROM hx_material_manage m INNER JOIN hx_material_detail d ON m.list_number = d.list_number LEFT JOIN hx_organization_unload v ON v.id IS NOT NULL AND v.id = m.send_company LEFT JOIN hx_organization_unload v2 ON v2.id IS NOT NULL AND v2.id = m.receive_company LEFT JOIN hx_user_unload v3 on v3.user_login_name = m.create_person INNER JOIN hx_fitting_detail ON hx_fitting_detail.fitting_code = d.fitting_code INNER JOIN v_code c ON c.code_id = 'fitting_type' AND c.code_key = m.type WHERE m.type='zb-n-cg'
AND m.list_number like CONCAT('%',#{param.listNumber},'%')
AND m.send_company like CONCAT('%',#{param.sendCompany},'%')
AND m.receive_company like CONCAT('%',#{param.receiveCompany},'%')
AND m.type = #{param.type}
AND hx_fitting_detail.parts_code like CONCAT('%',#{param.partsCode, jdbcType=VARCHAR},'%')
AND d.suit_model like CONCAT('%', #{param.suitModel}, '%')
AND d.fitting_code like CONCAT('%', #{param.fittingCode}, '%')
AND hx_fitting_detail.fitting_type = #{param.fittingType}
and DATE_FORMAT(m.update_time, '%Y-%m-%d') >= #{param.ksrq}
and DATE_FORMAT(m.update_time, '%Y-%m-%d') >= #{param.ksrq}
