mapper.select:
SELECT DISTINCT t.parcel_code, t.bills_code, t.carrier_unit, v2.name send_unit, t.update_date, v. NAME receive_unit, t.state status_hidden, v1.code_value state1 FROM hx_parcel_delivery t LEFT JOIN hx_organization_unload v ON v.id IS NOT NULL AND v.id = t.receive_unit LEFT JOIN hx_organization_unload v2 ON v2.id IS NOT NULL AND v2.id = t.send_unit LEFT JOIN v_code v1 on v1.code_id='ybzt' and v1.code_key = t.state JOIN hx_parcel_material pm ON pm.parcel_code = t.parcel_code AND list_number IN ( ${param.list} ) where t.state ='01'
and t.fitting_position_id =#{param.fittingPositionId}
and t.founders = #{param.founders}
and t.parcel_code like CONCAT('%',#{param.parcelCode},'%')
and t.bills_code like CONCAT('%',#{param.billsCode},'%')
and t.receive_unit like CONCAT('%',#{param.receiveUnit},'%')
and t.carrier_unit like CONCAT('%',#{param.carrierUnit},'%')
and DATA_FORMAT(t.update_date,'%Y-%m-%d') >=#{param.updateDate_sta}
and DATA_FORMAT(t.update_date,'%Y-%m-%d') <=#{param.updateDate_end}
